@screenshot_on_failure @pytest.mark.login @pytest.mark.compatibility @pytest.mark.asyncio async def test_login_invalid_password(app): """ Test login with a valid email but incorrect password. Verifies that the appropriate error message is displayed. """ await app.login_page.load_login_direct() await app.login_page.enter_email(PERSONAS["user"]["email"]) await app.login_page.click_continue() await app.login_page.enter_password("wrongpassword") await app.login_page.click_continue() # Assert error message for incorrect password is visible assert await app.login_page.error_message_email_or_password_incorrect.is_visible() assert app.login_page.error_message_password_incorrect_text == await app.login_page.get_error_message_password_incorrect_text() assert await app.login_page.has_email_or_password_incorrect_error_icon()
