# ------------------------------------------------------------------------------ # Test: Password with Too Many Characters (300+) # ------------------------------------------------------------------------------
@screenshot_on_failure @pytest.mark.login @pytest.mark.asyncio async def test_login_password_too_long(app): """ Test login with a password that exceeds the maximum character limit (300 characters). Verifies that the login fails with appropriate error message. """ await app.login_page.load_login_direct() too_long_password_string = "" await app.login_page.enter_email("valid@gmail.com") await app.login_page.click_continue() await app.login_page.enter_password(too_long_password_string) await app.login_page.click_continue() # Assert password error is shown assert await app.login_page.error_message_email_or_password_incorrect.is_visible() assert app.login_page.error_message_email_incorrect_text == await app.login_page.get_error_message_password_incorrect_text() assert await app.login_page.has_email_or_password_incorrect_error_icon()
# ------------------------------------------------------------------------------ # Test: Password Masking/Unmasking Functionality # ------------------------------------------------------------------------------
@screenshot_on_failure @pytest.mark.login @pytest.mark.asyncio async def test_password_shown_when_button_clicked(app): """ Test the password show/hide functionality to ensure passwords are properly masked and can be revealed when the show password button is clicked. """ await app.login_page.load_login_direct() await app.login_page.enter_email(PERSONAS["user"]["email"]) await app.login_page.click_continue() await app.login_page.enter_password("supersecret")
# Ensure password is hidden by default (type="password") input_type = await app.login_page.password_textbox.get_attribute("type") assert input_type == "password"
# Click the show password button to reveal the password await app.login_page.show_password_button.click() input_type_after = await app.login_page.password_textbox.get_attribute("type") assert input_type_after == "text" password_text = await app.login_page.get_password_text() assert password_text == "supersecret"
# Click again to hide the password await app.login_page.show_password_button.click() input_type_hidden = await app.login_page.password_textbox.get_attribute("type") assert input_type_hidden == "password"
